# -----------------------
# PARSEO: devolvemos lista de (nivel, dura) por trama
# -----------------------
def parse_csv(s):
    s = s.strip()
    parts = [p.strip() for p in s.split(",") if p.strip()]
    vals = [int(x) for x in parts]
    return [(1 if v < 0 else 0, abs(v)) for v in vals]


parsed = [parse_csv(t) for t in tramas]

# -----------------------
# OPCIONAL: eliminar pulso inicial de sincronización (muy largo)
# buscamos primer pulso > umbral_sync y quitamos ese primer elemento si existe
# -----------------------
SYNC_THRESHOLD = 50000  # 50 ms (ajusta si tu sync era ~297000us)
parsed_trimmed = []
for p in parsed:

    # si el primer elemento duración > SYNC_THRESHOLD, lo ignoramos (salta índice 0)
    if len(p) > 0 and p[0][1] > SYNC_THRESHOLD:
        parsed_trimmed.append(p[1:])  # eliminar primer par
    else:
        parsed_trimmed.append(p[:])

# -----------------------
# Para comparar: queremos alinear por índice de transición.
# Las tramas pueden tener ligeramente distinto número de transiciones.
# Calculamos la longitud máxima y rellenamos con None para las que no tengan
# -----------------------
max_len = max(len(x) for x in parsed_trimmed)
matrix_levels = np.full((len(parsed_trimmed), max_len), -1, dtype=int)  # -1 = no dato
matrix_dur = np.full((len(parsed_trimmed), max_len), 0, dtype=int)

for i, p in enumerate(parsed_trimmed):
    for j, (lvl, d) in enumerate(p):
        matrix_levels[i, j] = lvl
        matrix_dur[i, j] = d

# -----------------------
# Detectar índices donde hay diferencias significativas entre tramas
# criterio simple: dos duraciones se consideran iguales si |a-b| < tol_us
# también consideramos nivel (ALTO/BAJO) como parte de la comparación
# -----------------------
TOL_US = 200  # tolerancia en microsegundos para considerar duraciones iguales (ajusta)
diff_indices = []
for col in range(max_len):
    col_vals = matrix_dur[:, col]
    col_lvls = matrix_levels[:, col]

    # si alguna trama no tiene dato aquí, consideramos diferencia
    if np.any(col_lvls == -1):
        diff_indices.append(col)
        continue

    # comparar todos contra el primero
    base_d = col_vals[0]
    base_l = col_lvls[0]
    different = False
    for r in range(1, len(parsed_trimmed)):
        if col_lvls[r] != base_l:
            different = True
            break
        if abs(int(col_vals[r]) - int(base_d)) > TOL_US:
            different = True
            break
    if different:
        diff_indices.append(col)

# -----------------------
# Mostrar resumen por consola
# -----------------------
print(f"Numero de tramas: {len(parsed_trimmed)}. Max transiciones: {max_len}")
print(f"Indices con diferencias detectadas (tolerance {TOL_US} us): {diff_indices}")
print("\nDetalle de diferencias (index : [ (lvl,dur) por trama ])\n")
for idx in diff_indices:
    row = []
    for t in range(len(parsed_trimmed)):
        if matrix_levels[t, idx] == -1:
            row.append(("N/A", 0))
        else:
            row.append((matrix_levels[t, idx], int(matrix_dur[t, idx])))
    print(f"{idx:03d} : {row}")

# -----------------------
# PLOTEO: dibujar cada trama (offset vertical por trama) y resaltar diferencias
# -----------------------
plt.figure(figsize=(14, 6))
plt.title("Comparación de tramas (línea inicial alta removida)")
plt.xlabel("Tiempo acumulado (us)")
plt.ylabel("Trama (offset)")

colors = ["red", "blue", "green", "magenta", "orange"]

for t_idx, p in enumerate(parsed_trimmed):
    time = [0]
    level = [0]
    cur = 0
    for (lvl, d) in p:
        cur += d
        level.append(lvl)
        time.append(cur)

    # shift vertical para separar tramas
    y = np.array(level) + (len(parsed_trimmed) - 1 - t_idx) * 1.5
    plt.step(time, y, where="post", color=colors[t_idx % len(colors)])

